sys_ws_operation.operation_script:
(function process(/**RESTAPIRequest*/ request, /**RESTAPIResponse*/ response) {

    // Declare empty array for answer
    var answer = [];

    // Declare variables for the possible fields in the request body
    var make, model, vin, year, country, city;

    // Get the request body, a JSON object
    var requestBody = request.body;

    // Convert JSON object to a JavaScript string
    var requestString = requestBody.dataString;

    // Convert JavaScript string to a JSON object (array of vehicles)
    var result = JSON.parse(requestString);

    // For every vehicle in the array, create a record
    try {
        for (var i in result.vehicles) {
            make = result.vehicles[i].make;
            model = result.vehicles[i].model;
            vin = result.vehicles[i].vin;
            year = result.vehicles[i].year;
            country = result.vehicles[i].country;
            city = result.vehicles[i].city;

            // Insert the record into the database
            var vehicleGr = new GlideRecord('x_793388_vehicles_vehicles');
            vehicleGr.initialize();
            vehicleGr.make = make;
            vehicleGr.model = model;
            vehicleGr.vin = vin;
            vehicleGr.year = year;
            vehicleGr.country = country;
            vehicleGr.city = city;
            vehicleGr.insert();

            // Add the created record's sys_id and VIN to the response
            var vehicleObj = {
                "vin": vehicleGr.vin,
                "sys_id": vehicleGr.sys_id
            };
            answer.push(vehicleObj);
        }

        // Set the response status to 201 (Created)
        response.setStatus(201);

    } catch (error) {
        // Handle any errors during processing
        var serviceError = new sn_ws_err.ServiceError();
        serviceError.setStatus(500);
        serviceError.setMessage('Service error');
        serviceError.setDetail('The server could not process the request.');
        response.setError(serviceError);
    }

    // Generate the response body containing all vehicle records created
    response.setBody(answer);

})(request, response);
