sys_ws_operation.operation_script:
(function process(/**RESTAPIRequest*/ request, /**RESTAPIResponse*/ response) {

    // Declare empty array for response
    var answer = [];

    // Get the VIN from the path parameter
    var vin = request.pathParams.vin;

    // Validate if VIN is provided
    if (!vin) {
        response.setError(new sn_ws_err.NotFoundError('VIN not provided.'));
        response.setStatus(404);
        return;
    }

    // Query the vehicle table for the provided VIN
    var vehicleGr = new GlideRecord('x_793388_vehicles_vehicles'); // Replace with your table name
    vehicleGr.addQuery('vin', vin);
    vehicleGr.query();

    // Generate 404 if no matching record is found
    if (!vehicleGr.hasNext()) {
        response.setError(new sn_ws_err.NotFoundError('Vehicle not found.'));
        response.setStatus(404);
        return;
    }

    // For the vehicle found, delete it and create a response
    while (vehicleGr.next()) {
        vehicleGr.deleteRecord();

        // Create a JSON object with details of the deleted record
        var vehicleObj = {
            "vin": vehicleGr.getValue('vin'),
            "make": vehicleGr.getValue('make'),
            "model": vehicleGr.getValue('model'),
            "year": parseInt(vehicleGr.getValue('year'), 10),
            "city": vehicleGr.getValue('city'),
            "country": vehicleGr.getValue('country'),
            "message": "Vehicle deleted."
        };
        answer.push(vehicleObj);
    }

    // Generate the response body
    response.setBody(answer);

})(request, response);
